package exercicos.java;

public class Exercicio2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// SEGUNDO EXERCICo
		// CORRIGIR ESTE EXERCICIO PORQUE ELE ESTA ERRADO 
		int soma = 0; 
		int f = 0;
		while (f<=100) {
		soma = soma + f;
		f++;
		}
		
		System.out.println("Valor da soma:" + soma);
		
	}

}
